"  </div>",
"  <div class=\"headingAnchor\" id=\"references\">",
"   <h1>",
"    REFERENCES",
"   </h1>",
"   <ol id=\"reference\">",
"    <li>",
"     <div class=\"reference\">",
"      Boyer LV, Theodorou AA, Berg RA, et al, \"Antivenom for Critically Ill Children With Neurotoxicity From Scorpion Stings,\"",
"      <i>",
"       N Engl J Med",
"      </i>",
"      , 2009, 360(20):2090-8.",
"      <span class=\"pubmed-id\">",
"       [PubMed",
"       <a href=\"mobipreview.htm?38/44/39619/abstract-text/19439743/pubmed\" id=\"19439743\" target=\"_blank\">",
"        19439743",
"       </a>",
"       ]",
"      </span>",
"     </div>",
"    </li>",
