var title_f38_47_39676="Rx HACEK endocarditis";
var content_f38_47_39676=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F70411&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F70411&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested regimens for therapy of native valve endocarditis due to HACEK* microorganism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"       American Heart Association",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"       British Society for Antimicrobial Chemotherapy",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"       European Society of Cardiology",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       Adult (for patients with normal renal function)",
"      </td>",
"      <td class=\"subtitle2\">",
"       Pediatric (not to exceed dose of normal adult)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <p>",
"        <strong>",
"         One of the following:",
"        </strong>",
"       </p>",
"       <p>",
"        Ceftriaxone&bull; 2 g per 24 h IV or IM in 1 dose for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ampicillin-sulbactam 12 g per 24 h IV in 4 equally divided doses for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ciprofloxacin&sect; 1000 mg per 24 h PO or 800 mg per 24 h IV in 2 equally divided doses for 4 wks",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        <strong>",
"         One of the following:",
"        </strong>",
"       </p>",
"       <p>",
"        Ceftriaxone&bull; 100 mg/kg per 24 h IV or IM in 1 dose for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ampicillin-sulbactam 300 mg/kg per 24 h IV in 4 equally divided doses doses for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ciprofloxacin&sect; 20-30 mg/kg per 24 h IV or PO in 2 equally divided doses for 4 wks",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        <strong>",
"         Either",
"        </strong>",
"       </p>",
"       <p>",
"        Ampicillin&Delta; 2 g IV every 4 to 6 h for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ceftriaxone&bull; 2 to 4 g per 24 h IV or IM in 1 dose for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         plus",
"        </strong>",
"       </p>",
"       <p>",
"        Gentamicin&loz; 1 mg/kg IV every 8 to 12 h for 2 wks",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        <strong>",
"         One of the following:",
"        </strong>",
"       </p>",
"       <p>",
"        Ceftriaxone&bull; 2 g per 24 h IV or IM in 1 dose for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Combination therapy with: ampicillin&Delta; 12 g per 24 h IV in 4 or 6 equally divided doses",
"        <strong>",
"         plus",
"        </strong>",
"        gentamicin&loz; 3 mg/kg per 24 h IV or IM in 2 or 3 equally divided doses for 4 wks",
"       </p>",
"       <p>",
"        <strong>",
"         or",
"        </strong>",
"       </p>",
"       <p>",
"        Ciprofloxacin&sect; 1000 mg per 24 h PO or 800 mg per 24 h IV in 2 equally divided doses for 4 wks",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IV: intravenously; IM: intramuscularly; PO: orally; h: hours; wks: weeks",
"     <br>",
"      * Haemophilus parainfluenzae, Haemophilus aphrophilus, Actinobacillus actinomycetemcomitans, Cardiobacterium hominis, Eikenella corrodens, and Kingella kingae.",
"      <br>",
"       &bull; Cefotaxime or another third- or fourth-generation cephalosporin may be substituted.",
"       <br>",
"        &Delta; If susceptibility to ampicillin has been demonstrated.",
"        <br>",
"         &loz; Gentamicin dosage adjusted for peak serum concentration of 3-4 &micro;/mL, trough &lt;1 &micro;/mL.",
"         <br>",
"          &sect; Fluoroquinolone therapy recommended only for patients unable to tolerate cephalosporin and ampicillin therapy; levofloxacin, or moxifloxacin may be substituted. Use with caution in patients &lt;18 years old.",
"         </br>",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Circulation 2005;111:3167; J Antimicrob Chemother 2004;54:971; Eur Heart J 2009;30:2369.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
