" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of the adult with headache in the emergency department",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     F Michael Cutrer, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Jerry W Swanson, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Robert S Hockberger, MD, FACEP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Deputy Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     Jonathan Grayzel, MD, FAAEM",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39704/contributors\">",
"     John F Dashe, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?38/49/39704/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
"   <a href=\"/home/editorial-policy\" target=\"_blank\">",
"    peer review process",
"   </a>",
"   <span>",
"    is complete.",
"   </span>",
"  </div>",
"  <div id=\"literatureReviewDate\">",
"   <span class=\"emphasis\">",
"    Literature review current through:",
"   </span>",
"   Oct 2013.",
"   <span class=\"pipeSpace\">",
"    |",
"   </span>",
"   <span class=\"emphasis\">",
