" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of indications for and complications of ERCP and endoscopic biliary sphincterotomy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Silvano Loperfido, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Guido Costamagna, MD, FACG",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Douglas A Howell, MD, FASGE, FACG",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?38/49/39705/contributors\">",
"     Anne C Travis, MD, MSc, FACG",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
