var title_f38_49_39708="Transudative pleural effusions";
var content_f38_49_39708=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F73530&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F73530&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of transudative pleural effusions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Causes of transudative effusions",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Processes that",
"        <em>",
"         always",
"        </em>",
"        cause a transudative effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atelectasis",
"       </td>",
"       <td>",
"        Caused by increased intrapleural negative pressure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cerebrospinal fluid leak into pleural space",
"       </td>",
"       <td>",
"        Thoracic spinal surgery or trauma and ventriculopleural shunts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Heart failure",
"       </td>",
"       <td>",
"        Acute diuresis can result in borderline exudative features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatic hydrothorax",
"       </td>",
"       <td>",
"        Rare without clinical ascites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypoalbuminemia",
"       </td>",
"       <td>",
"        Edema fluid rarely isolated to pleural space",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Iatrogenic",
"       </td>",
"       <td>",
"        Misplaced intravenous catheter into the pleural space; post Fontan procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nephrotic syndrome",
"       </td>",
"       <td>",
"        Usually subpulmonic and bilateral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Peritoneal dialysis",
"       </td>",
"       <td>",
"        Acute massive effusion develops within 48 hours of initiating dialysis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Urinothorax",
"       </td>",
"       <td>",
"        Caused by ipsilateral obstructive uropathy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Processes that",
"        <em>",
"         may",
"        </em>",
"        cause a transudative effusion, but",
"        <em>",
"         usually",
"        </em>",
"        cause an exudative effusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amyloidosis",
"       </td>",
"       <td>",
"        Often exudative due to disruption of pleural surfaces",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chylothorax",
"       </td>",
"       <td>",
"        Most are exudative effusions&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Constrictive pericarditis",
"       </td>",
"       <td>",
"        Bilateral effusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypothyroid pleural effusion",
"       </td>",
"       <td>",
"        From hypothyroid heart disease or hypothyroidism per se",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Malignancy",
"       </td>",
"       <td>",
"        Usually exudative, but 3 to 10 percent transudative possibly due to early lymphatic obstruction, obstructive atelectasis, or concomitant disease (eg, heart failure)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulmonary embolism",
"       </td>",
"       <td>",
"        Most are exudative effusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sarcoidosis",
"       </td>",
"       <td>",
"        Stage II and III disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Superior vena caval obstruction",
"       </td>",
"       <td>",
"        May be due to acute systemic venous hypertension or acute blockage of thoracic lymph flow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Trapped lung",
"       </td>",
"       <td>",
"        A result of remote or chronic inflammation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
