"         <center>",
"          <span class=\"medCalcFontIO\">",
"           Input:",
"          </span>",
"          <br/>",
"          &nbsp;",
"          <br/>",
"          <table cellpadding=\"3\" cellspacing=\"0\" summary=\"MedCalc 3000 Table\">",
"           <tr>",
"            <td align=\"right\" width=\"42%\">",
"             <span class=\"medCalcFontOneBold\">",
"              CRP",
"             </span>",
"            </td>",
"            <td align=\"left\" nowrap=\"nowrap\" valign=\"top\" width=\"5%\">",
