"              <option value=\"1000|0|gm/dL\">",
"               gm/dL",
"              </option>",
"              <option value=\"0.001|0|mcg/dL\">",
"               mcg/dL",
"              </option>",
"              <option value=\"0.1|0|mcg/mL\">",
"               mcg/mL",
"              </option>",
"              <option value=\"1|0|mg%\">",
"               mg%",
"              </option>",
"              <option selected=\"selected\" value=\"1|0|mg/dL\">",
"               mg/dL",
"              </option>",
"              <option value=\"100|0|mg/mL\">",
"               mg/mL",
"              </option>",
"              <option value=\"0.0001|0|ng/mL\">",
"               ng/mL",
"              </option>",
"             </select>",
"            </td>",
"           </tr>",
"           <tr>",
"            <td align=\"right\" width=\"42%\">",
"             <span class=\"medCalcFontOneBold\">",
"              PGA",
"             </span>",
"            </td>",
"            <td align=\"left\" nowrap=\"nowrap\" valign=\"top\" width=\"5%\">",
"             &nbsp;",
"             <input name=\"PGA_param\" onblur=\"RheumatoidArthritisSDAI_fx(); minMaxCheck();\" onchange=\"RheumatoidArthritisSDAI_fx();\" size=\"6\" type=\"text\" value=\"\"/>",
"            </td>",
"            <td align=\"left\" valign=\"top\" width=\"115\">",
"             <select class=\"medCalcFontSelect\" name=\"PGA_unit\" onchange=\"RheumatoidArthritisSDAI_fx();\" style=\"width:105px;\">",
"              <option selected=\"selected\" value=\"1|0|#\">",
"               #",
