var title_f38_52_39755="Classification dystonia";
var content_f38_52_39755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F59278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F59278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of dystonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\" rowspan=\"2\">",
"        Age of onset",
"       </td>",
"       <td>",
"        Early (young) onset: &lt;26 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Late (adult) onset: &ge;26 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" rowspan=\"5\">",
"        Body distribution",
"       </td>",
"       <td>",
"        Focal: single body region",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Segmental: contiguous body regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Generalized: both legs and at least one other body region",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multifocal: noncontiguous body regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemidystonia: one half of the body",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" rowspan=\"8\">",
"        Etiology",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Primary:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dystonia is the only sign; may be sporadic or inherited",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Secondary:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dystonia associated with a defined etiology or associated with other neurological abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Dystonia plus: dystonia is a prominent sign, but is associated with another movement disorder. There is no evidence of neurodegeneration.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Heredodegenerative: dystonia is a prominent sign, along with other neurological features, of a heredodegenerative disorder.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Genetic:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        May be primary or secondary",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
