" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Standard immunizations for children and adolescents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
