var title_f38_6_39019="Test early detection CRC";
var content_f38_6_39019=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F58094&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F58094&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Testing options for the early detection of colorectal cancer and adenomatous polyps for asymptomatic adults aged 50 years and older (Multi-Society Task Force)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Tests that detect adenomatous polyps and cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FSIG every 5 years,",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CSPY every 10 years,",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DCBE every 5 years,",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CTC every 5 years*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Tests that primarily detect cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Annual gFOBT with high test sensitivity for cancer,",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Annual FIT with high test sensitivity for cancer,",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        sDNA, with high sensitivity for cancer, interval uncertain",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     FSIG: flexible sigmoidoscopy; CSPY: colonoscopy; DCBE: double-contrast barium enema; CTC: computed tomography colonography; gFOBT: guaiac-based fecal occult blood test; FIT: fecal immunochemical test; sDNA: stool DNA test.",
"     <br>",
"      * CTC, available at the time of the development of these guidelines, is no longer commercially available in 2012.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Levin, B, Lieberman, DA, McFarland, B, et al. Screening and Surveillance for the Early Detection of Colorectal Cancer and Adenomatous Polyps, 2008: A Joint Guideline From the American Cancer Society, the US Multi-Society Task Force on Colorectal Cancer, and the American College of Radiology. Gastroenterology, 134(5), 2008. Copyright &copy;2008 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
