var title_f38_6_39020="Coombs test in AIHA";
var content_f38_6_39020=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F69817&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F69817&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Significance of the pattern of positive direct antiglobulin (Coombs) test in the diagnosis of autoimmune hemolytic anemia (AIHA)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Anti-IgG",
"       </td>",
"       <td class=\"subtitle1\">",
"        Anti-C3",
"       </td>",
"       <td class=\"subtitle1\">",
"        Occurrence",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antibody",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antigen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        AIHA",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        Rh protein complex",
"       </td>",
"       <td>",
"        Not seen in SLE; aldomet-induced",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        Drug-induced",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        Drug attached to Rh complex",
"       </td>",
"       <td>",
"        Penicillin and aldomet",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Drug-induced",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        ?",
"       </td>",
"       <td>",
"        Fludarabine and cogeners",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        AIHA",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        Glycoprotein",
"       </td>",
"       <td>",
"        Antibody fixes complement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Drug-induced",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        Drug + protein",
"       </td>",
"       <td>",
"        Drug affixed to protein; needed to detect in serum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Drug-induced",
"       </td>",
"       <td>",
"        IgM or IgG",
"       </td>",
"       <td>",
"        Drug + protein",
"       </td>",
"       <td>",
"        Drug not firmly affixed to protein; drug needed to detect serum antibody",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        AIHA",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        ?",
"       </td>",
"       <td>",
"        Antibody of low affinity (may be detected with enzyme treated red cells)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        AIHA",
"       </td>",
"       <td>",
"        IgM or IgA",
"       </td>",
"       <td>",
"        ?",
"       </td>",
"       <td>",
"        Warm-reacting antibody detected with specific anti-isotype antisera",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Cold agglutinin disease",
"       </td>",
"       <td>",
"        IgM",
"       </td>",
"       <td>",
"        Polysaccharide",
"       </td>",
"       <td>",
"        Cold agglutinins in high titer in serum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        -",
"       </td>",
"       <td>",
"        +",
"       </td>",
"       <td>",
"        Paroxysmal cold hemoglobinuria",
"       </td>",
"       <td>",
"        IgG",
"       </td>",
"       <td>",
"        P antigen (polysaccharide)",
"       </td>",
"       <td>",
"        Antibody fixes complement in the cold. Hemolysis occurs when sample/patient is warmed. Donath-Landsteiner test may be positive.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AIHA: autoimmune hemolytic anemia; SLE: systemic lupus erythematosus.",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Wendell F Rosse, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
