" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Systemic treatment for advanced hepatocellular carcinoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/61/39898/contributors\">",
"     Author",
"    </a>",
