" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Biliary atresia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?38/63/39929/contributors\">",
