var title_f38_7_39036="Management strep TSS";
var content_f38_7_39036=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F57969&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F57969&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rapid empiric management of streptococcal toxic shock syndrome",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Diagnosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Isolation of Group A streptococcus (GAS) from a normally sterile site (eg, blood cerebrospinal, pleural, or peritoneal fluid, tissue biopsy, or surgical wound)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        and",
"       </strong>",
"       hypotension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        plus",
"       </strong>",
"       evidence of failure of two of more organ systems:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Renal failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Coagulopathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Liver involvement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Adult respiratory distress syndrome",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Erythematous macular rash",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Soft tissue necrosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Management",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Hemodynamic support",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Massive amounts of intravenous fluids",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Vasopressors (eg, dopamine and/or norepinephrine)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Surgical therapy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Prompt and aggressive exploration and debridement of suspected sites of infection",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Consider surgical intervention in patients who have fever and excruciating pain, particularly with soft tissue swelling or formation of violaceous vesicles or bullae",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Empiric antibiotics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Immediately begin empiric antibiotics, following culture of blood and suspected site of infection, with broad-spectrum antibiotics including:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clindamycin (900 mg IV every eight hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <strong>",
"        plus",
"       </strong>",
"       one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       A carbapenem (eg, imipenem 500 mg every six hours",
"       <strong>",
"        or",
"       </strong>",
"       meropenem 1 g every eight hours)",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       A combination drug containing a penicillin plus beta-lactamase inhibitor 1 g every eight hours) (eg, ticarcillin-clavulanate 3.1 g every four hours",
"       <strong>",
"        or",
"       </strong>",
"       piperacillin-tazobactam 4.5 g every six hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Tailored antibiotics (eg, once diagnosis of toxic shock syndrome due to GAS is established)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clindamycin (900 mg IV every eight hours)",
"       <strong>",
"        plus",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Penicillin G (4 million units IV every four hours)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Intravenous immune globulin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Treatment with intravenous immune globulin (1 g/kg day one, followed by 0.5 g/kg days two and three)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
