var title_f39_0_39947="Properties of hydroxyurea";
var content_f39_0_39947=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F63410&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F63410&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical properties of hydroxyurea",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Characteristic",
"      </td>",
"      <td class=\"subtitle1\">",
"       Hydroxyurea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Drug class",
"      </td>",
"      <td>",
"       Antimetabolite",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Mechanism of action",
"      </td>",
"      <td>",
"       Not genotoxic, impairs DNA repair by inhibiting ribonucleotide reductase",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Specificity",
"      </td>",
"      <td>",
"       Affects all cell lines",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pharmacology",
"      </td>",
"      <td>",
"       Half-life four hours, renal excretion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Starting dose",
"      </td>",
"      <td>",
"       15 mg/kg per day PO",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Onset of action",
"      </td>",
"      <td>",
"       Three to five days when the usual starting dose is employed (15 mg/kg per day).*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Side effects observed in &gt;10 percent of patients",
"      </td>",
"      <td>",
"       Neutropenia, anemia, oral ulcers, hyperpigmentation, rash, nail changes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Side effects observed in &le;10 percent of patients",
"      </td>",
"      <td>",
"       Ankle ulcers, lichen planus-like lesions of the mouth and skin, nausea, diarrhea",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rare side effects",
"      </td>",
"      <td>",
"       Fever, liver function test abnormalties",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Contraindications",
"      </td>",
"      <td>",
"       Neutropenia, pregnancy, chilbearing potential, breast feeding",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Annual cost",
"      </td>",
"      <td>",
"       $1700.00, for 500 mg three times daily dose",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The onset of action is 24 to 48 hours when a higher dose of hydroxyurea is used for hyperleukocytosis (ie, 50-100 mg/kg per day).",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
