var title_f39_1_39963="Juvenile fibromyalgia criteria";
var content_f39_1_39963=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F69755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F69755&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Yunis and Masi criteria for juvenile primary fibromyalgia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       The diagnosis for juvenile primary fibromyalgia is based upon all four major criteria plus 3 of the 10 minor criteria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Major criteria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       1. Generalized musculoskeletal aching at &ge;3 sites for at least 3 months",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       2. Absence of an underlying condition or cause (eg, arthritis or trauma)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       3. Normal test results",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       4. Five tender points",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Minor criteria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       1. Chronic anxiety or tension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       2. Fatigue",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       3. Poor sleep",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       4. Chronic headaches",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       5. Irritable bowel syndrome",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       6. Subjective soft tissue swelling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       7. Numbness",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       8. Pain modulation of physical activity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       9. Pain modulation by weather factors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       10. Pain modulation by anxiety and/or stress",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Yunis, MB, Masi, AT. Juvenile primary fibromyalgia syndrome. A clinical study of thirty-three patients and matched normal controls. Arthritis Rheum 1985; 28:138.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
