var title_f39_11_40123="Criteria for dyssynergic defecation";
var content_f39_11_40123=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F61867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F61867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for dyssynergic defecation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <strong>",
"         A.",
"        </strong>",
"        Patients must satisfy the diagnostic criteria for functional chronic constipation (Rome III)",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         B.",
"        </strong>",
"        Patients must demonstrate dyssynergia during repeated attempts to defecate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Dyssynergic or obstructive pattern of defecation (types 1-4) is defined as:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent3\">",
"        Paradoxical increase in anal sphincter pressure (anal contraction)",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent3\">",
"        Less than 20 percent relaxation of the resting anal sphincter pressure",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent3\">",
"        Inadequate propulsive forces observed with manometry, imaging, or EMG recordings",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent3\">",
"        One or more of the following criteria during repeated attempts to defecate:",
"        <ul>",
"         <li>",
"          Inability to expel an artificial stool (50 mL water-filled balloon) within 1 minute",
"         </li>",
"         <li>",
"          A prolonged colonic transit time (ie, greater than five markers [&gt;20 percent marker retention]) on a plain abdominal radiograph taken 120 hours after ingestion of one sitzmark capsule containing 24 radiopaque markers",
"         </li>",
"         <li>",
"          Inability to evacuate or &ge;50 percent retention of barium during defecography",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Rao SSC. Dyssynergic defecation and biofeedback therapy. Gastroenterol Clin N Am 2008; 37:569. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
