var title_f39_13_40155="DSM IV criteria for dementia";
var content_f39_13_40155=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F66244&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F66244&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV criteria for dementia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       1. Memory impairment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       2. At least one of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Aphasia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Apraxia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Agnosia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Disturbance in executive functioning",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. The disturbance in 1 and 2 significantly interferes with work, social activities, or relationships",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       4. Disturbance does not occur exclusively during delirium",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Additional criteria for dementia type:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Dementia of the Alzheimer type:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gradual onset and continuing cognitive decline",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Not caused by identifiable medical, psychiatric, or neurologic condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Vascular dementia:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Focal neurological signs or laboratory evidence of cerebrovascular condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Dementia due to other medical conditions:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Evidence from history, physical exam, or laboratory findings of a specific medical condition causing cognitive deficits (HIV disease, head trauma, Parkinson disease, Huntington's chorea, Pick's disease, Creutzfeld-Jacob)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from American Psychiatric Association Diagnostic and Statistical Manual, 4th ed, APA Press, Washington DC, 1994.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
