var title_f39_25_40349="Diagnostic studies for pediatric respiratory distress";
var content_f39_25_40349=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F82210&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F82210&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic studies for evaluation of acute respiratory distress",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test",
"       </td>",
"       <td class=\"subtitle1\">",
"        Indications",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Bedside testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulse oximetry",
"       </td>",
"       <td>",
"        All patients with respiratory distress",
"       </td>",
"       <td>",
"        <p>",
"         Normal values despite hypoxia seen in patients with severe anemia, carboxyhemoglobin, or sickle cell disease",
"        </p>",
"        Falsely low values obtained in patients with pulse oximeter not correlating with pulse, poor peripheral perfusion, venous congestion, methemoglobinemia, certain nail polish colors, and in patients receiving vital dyes (eg, methylene blue) during surgical procedures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        End-tidal CO2 (ETCO2) measurement",
"       </td>",
"       <td>",
"        <p>",
"         Confirmation of successful endotracheal intubation",
"        </p>",
"        <p>",
"         Noninvasive monitoring of ventilation in intubated and non-intubated patients",
"        </p>",
"        Noninvasive monitoring for sedation in children",
"       </td>",
"       <td>",
"        Measurable in non-intubated and intubated patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Electrocardiogram",
"       </td>",
"       <td>",
"        Clinical suspicion of cardiac disease (eg, cardiac murmur, gallop, differential pulses or blood pressure between upper and lower extremities)",
"       </td>",
"       <td>",
"        Typically combined with chest radiograph to assess heart size and pulmonary vasculature in order to determine need for echocardiography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Laboratory testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Arterial blood gas",
"       </td>",
"       <td rowspan=\"2\">",
"        <p>",
"         Determine PaO2 for calculation of physiologic measures of oxygenation (eg, A-a gradient, PaO2/FiO2 ratio)",
"        </p>",
"        <p>",
"         Correlate pCO2 with end-tidal CO2 measurments",
"        </p>",
"        Measure pH and correlate with venous pH",
"       </td>",
"       <td>",
"        End-tidal CO2, pulse oximetry, and venous blood gases may be used as less invasive methods for ongoing monitoring of oxygenation, ventilation, and acid-base status if they correlate with arterial blood gas measurements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Assesses for the presence of an anion gap and renal dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Electrolytes, blood urea nitrogen, creatinine",
"       </td>",
"       <td>",
"        Patients with metabolic acidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glucose",
"       </td>",
"       <td>",
"        Altered mental status",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ammonia",
"       </td>",
"       <td>",
"        Altered mental status and other findings suggestive of urea cycle defects",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Carboxyhemoglobin",
"       </td>",
"       <td>",
"        <p>",
"         Smoke inhalation",
"        </p>",
"        Altered mental status, headache, vomiting and possible exposure to carbon monoxide (eg, blocked furnace flue)",
"       </td>",
"       <td>",
"        Pulse oximetry is falsely elevated in the presence of carboxyhemoglobin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methemoglobin",
"       </td>",
"       <td>",
"        <p>",
"         Cyanosis in the presence of a normal PaO2 on arterial blood gas",
"        </p>",
"        Exposure to agents known to cause methemoglobinemia (eg, nitrites, benzocaine, aniline dyes)",
"       </td>",
"       <td>",
"        Oxygen saturation by cooximetry identifies the presence of an abnormal hemoglobin if specific measure of methemoglobin is not available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        D-dimer",
"       </td>",
"       <td>",
"        Clinical findings suggestive of pulmonary embolus (eg, low oxygenation, pleuritic chest pain, wedge-shaped infiltrate on chest radiograph, and predisposing condition [eg, sickle cell disease, thrombotic condition])",
"       </td>",
"       <td>",
"        <p>",
"         Pulmonary embolus is a rare cause of respiratory distress in children",
"        </p>",
"        Imaging options in patients with moderate to high clinical probability and elevated D-dimer include ventilation/perfusion scan, ultrasound of extremity veins and deep veins, CT pulmonary angiography, and pulmonary angiography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Imaging",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lateral neck radiograph",
"       </td>",
"       <td>",
"        Clinical findings suggestive of epiglottitis, retropharyngeal abscess, or ingested foreign body",
"       </td>",
"       <td>",
"        Croup can usually be diagnosed clinically without a radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chest radiograph",
"       </td>",
"       <td>",
"        All children with significant respiratory distress and those with focal lung findings",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Forced expiratory or bilateral decubitus chest radiograph",
"       </td>",
"       <td>",
"        Suspected foreign body aspiration",
"       </td>",
"       <td>",
"        Hyperaeration noted on the side with the bronchial foreign body",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Unilateral decubitus chest radiograph",
"       </td>",
"       <td>",
"        Assess whether lung opacity is due to parenchymal disease or effusion",
"       </td>",
"       <td>",
"        Loculated effusions and very large effusions may not show evidence of layering",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Echocardiography",
"       </td>",
"       <td>",
"        Assess cardiac function and presence of structural heart disease",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abdominal radiographs (supine and upright, or cross-table lateral)",
"       </td>",
"       <td>",
"        Significant abdominal tenderness and/or distension with concern for intestinal obstruction or perforation",
"       </td>",
"       <td>",
"        Other testing (eg, ultrasound, upper gastrointestinal contrast study, abdominal CT) may also be indicated depending upon clinical findings and likely etiologies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Computed tomography (CT) of the head",
"       </td>",
"       <td>",
"        Clinical findings suggestive of increased intracranial pressure or intracranial mass lesion",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
