" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Complications and screening in children and adolescents with type 1 diabetes mellitus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Lynne L Levitsky, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Madhusmita Misra, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Joseph I Wolfsdorf, MB, BCh",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/29/40410/contributors\">",
"     Alison G Hoppin, MD",
"    </a>",
"    <br/>",
"   </div>",
