var title_f39_29_40411="Rx bacterial vaginosis";
var content_f39_29_40411=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F77648&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F77648&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment options for bacterial vaginosis (nonpregnant women)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metronidazole 500 mg oral twice daily for seven days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metronidazole gel 0.75 percent 5 g* (one full applicator) intravaginally once daily for five days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin 2 percent cream 5 g",
"        <sup>",
"         &bull;",
"        </sup>",
"        (one full applicator) intravaginally at bedtime for seven days",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin 300 mg orally twice daily for seven days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clindamycin ovule (vaginal suppository) 100 mg intravaginally once daily for three days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tinidazole 2 g orally for two days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tinidazole 1 g orally once daily for five days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Although data are limited, metronidazole 750 mg extended release tablets once daily for seven days OR a single vaginal dose of clindamycin 2 percent bioadhesive cream (US trade name: Clindesse) 5 g",
"    <sup>",
"     &bull;",
"    </sup>",
"    , one full applicator, also appear to be effective.",
"    <div class=\"footnotes\">",
"     * 5 grams of metronidazole 0.75 percent vaginal gel contains 37.5 mg of metronidazole.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     5 grams of clindamycin 2 percent vaginal cream contains 100 mg of clindamycin.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Sexually Transmitted Diseases Treatment Guidelines, 2010. MMWR Recomm Rep 2010 (RR-12); 59.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
