" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Cellular and molecular biology of chronic myeloid leukemia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/33/40474/contributors\">",
"     Author",
"    </a>",
