"      <tr>",
"       <td rowspan=\"4\">",
"        D",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td rowspan=\"4\">",
"        Diffuse seeds",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Retinoblastoma with",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        D1",
"       </td>",
"       <td class=\"sublist1\">",
"        Subretinal seeds &gt;3 mm from retinoblastoma",
