" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Evaluation of dysphagia in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/39/40569/contributors\">",
