" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathogenesis, epidemiology, and clinical manifestations of celiac disease in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/44/40650/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/44/40650/contributors\">",
"     Detlef Schuppan, MD, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/44/40650/contributors\">",
"     Walburga Dieterich, PhD",
"    </a>",
"    <br/>",
