var content_f39_46_40683=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56279&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56279&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Screening recommendations for type 2 diabetes mellitus in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Overweight status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Body mass index &ge;85th percentile for age and gender",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Weight for height &ge;85th percentile",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Weight &ge;120 percent of ideal for height",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Plus any two of the following risk factors:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Family history of type 2 diabetes mellitus in a 1",
"        <sup>",
"         st",
"        </sup>",
"        or 2",
"        <sup>",
"         nd",
"        </sup>",
"        degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        High-risk race/ethnicity (Native American, African-American, Latino, Asian American, Pacific Islander)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Signs of insulin resistance on physical examination or conditions associated with insulin resistance (Acanthosis nigricans, hypertension, dyslipidemia, polycystic ovary syndrome, or small-for-gestational age birthweight)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Maternal history of diabetes or gestational diabetes mellitus during the child's gestation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Screening frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Begin screening at age 10 years, or at onset of puberty if this occurs less than 10 years old",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Repeat screening every three years",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     American Diabetes Association. Standards of medical care in diabetes--2013. Diabetes Care 2013; 36 Suppl 1:S11.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
