var title_f39_46_40684="Echo asymptomatic murmurs";
var content_f39_46_40684=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F50108%7ECARD%2F58419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F50108%7ECARD%2F58419&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Echocardiography in asymptomatic patients with cardiac murmurs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general agreement that echocardiography is useful in asymptomatic patients with the following cardiac murmurs:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Diastolic murmurs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Continuous murmurs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Late systolic murmurs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Murmurs associated with ejection clicks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Murmurs that radiate to the neck or back",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Grade 3 or louder midpeaking systolic murmurs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of evidence or opinion is in favor of usefulness of echocardiography in asymptomatic patients with the following cardiac murmurs:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Murmurs associated with other abnormal physical findings on cardiac examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Murmurs associated with an abnormal electrocardiogram or chest x-ray",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence and/or general agreement that echocardiography is not useful in asymptomatic patients with the following murmurs:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Grade 2 or softer midsystolic murmurs considered innocent or functional by an experienced observer",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Echocardiography in symptomatic patients with cardiac murmurs",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Class I - There is evidence and/or general agreement that echocardiography is useful in symptomatic patients with heart murmurs who also have one or more of the following",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Symptoms or signs of heart failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Myocardial ischemia or infarction",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Syncope",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Thromboembolism",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Infective endocarditis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Other evidence of structural heart disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Class IIa - The weight of evidence or opinion is in favor of the usefulness of echocardiography in the following patients with heart murmurs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &bull; Symptoms or signs are probably noncardiac in origin but a cardiac cause cannot be excluded by standard evaluation.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
