var title_f39_48_40715="Viral versus bacterial sinusitis";
var content_f39_48_40715=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F83921&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F83921&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristic features of viral versus bacterial rhinosinusitis in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical feature",
"       </td>",
"       <td class=\"subtitle1\">",
"        Viral rhinosinusitis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Bacterial rhinosinusitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fever",
"       </td>",
"       <td>",
"        Fever is typically absent, but when present",
"        <strong>",
"         occurs early (in first 24 hours)",
"        </strong>",
"        , is low-grade, and resolves within the first&nbsp;two days",
"       </td>",
"       <td>",
"        In severe presentation, temperature may be &ge;39&deg;C (102.2&deg;F) for &gt;3 days; in worsening presentation, fever may develop or recur on day 6 to 7 of illness after initial improvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nasal discharge",
"       </td>",
"       <td>",
"        Peaks on days 3 to 6 of illness and then steadily improves",
"       </td>",
"       <td>",
"        Fails to improve or worsens over time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cough",
"       </td>",
"       <td>",
"        Peaks on days 3 to 6 of illness and then steadily improves",
"       </td>",
"       <td>",
"        Fails to improve or worsens over time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ill-appearance",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        May occur (in severe presentation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe headache",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        May be a sign of severe illness or complication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clinical course",
"       </td>",
"       <td>",
"        Symptoms peak in severity on days 3 to 6 and then improve",
"       </td>",
"       <td>",
"        Symptoms are present for &ge;10 days",
"        <strong>",
"         without improvement",
"        </strong>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
