var title_f39_48_40716="Foods that contain gluten PI";
var content_f39_48_40716=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F86922&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F86922&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Foods that contain gluten",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Foods that definitely contain gluten:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wheat (einkorn, durum, faro, graham, kamut, semolina, spelt)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rye",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Barley",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malt, malt flavoring, malt extract derived from barley",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malt vinegar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Triticale",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Foods that might contain gluten:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beers, ales, and lagers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breading, bread crumbs, and coating mixes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brewer's yeast",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Broth, bouillon, or soup stock",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brown rice syrup",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cake frosting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Candy and licorice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Communion wafers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Condiments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Croutons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dates (if rolled in oat flour)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drink mixes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Energy bars",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flavored teas and coffees",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flour or cereal products",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Imitation products, like bacon or seafood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Matzo and matzo meal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medicines (both over-the-counter and prescription)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oats (unless they are labeled \"gluten free\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pasta",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Processed lunch meats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rice pilaf",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Salad dressing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sauces, gravies, marinades, and thickeners",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seasonings, spices, and smoke flavoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Seasoned chips, nuts, and seeds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Self-basting poultry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Soy sauce",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stuffing (for poultry)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamins and herbal supplements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Play products for children:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Play-dough",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paper mache",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Glue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Some of these play products are made from flour or contain gluten. Children with celiac disease can play with these products, but they should wash their hands after playing.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    For the foods that might contain gluten, it depends on their ingredients and where they were made. To know for sure, you will need to check the ingredient label.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
