var title_f39_52_40780="Indications for ICD in patients with unexplained syncope";
var content_f39_52_40780=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F73650&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F73650&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for ICD in patients with unexplained syncope and high risk of SCD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical situation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Class*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Level",
"        <span class=\"small\">",
"         &bull;",
"        </span>",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In patients with ischaemic cardiomyopathy with severely depressed LVEF or HF, ICD therapy is indicated according to current guidelines for ICD&mdash;cardiac resynchronization therapy implantation",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In patients with non-ischaemic cardiomyopathy with severely depressed LVEF or HF, ICD therapy is indicated according to current guidelines for ICD&mdash;cardiac resynchronization therapy implantation",
"       </td>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        A",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In hypertrophic cardiomyopathy ICD therapy should be considered in patients at high risk (see text)",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        In non-high risk, consider ILR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In right ventricular cardiomyopathy ICD therapy should be considered in patients at high risk (see text)",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        In non-high risk, consider ILR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In Brugada syndrome ICD therapy should be considered in patients with spontaneous type I ECG",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        B",
"       </td>",
"       <td>",
"        In the absence of spontaneous type I pattern, consider ILR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In long QT syndrome, ICD therapy, in conjunction with &beta;-blockers, should be considered in patients at risk",
"       </td>",
"       <td>",
"        IIa",
"       </td>",
"       <td>",
"        B",
"       </td>",
"       <td>",
"        In non-high risk, consider ILR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In patients with ischaemic cardiomyopathy without severely depressed LVEF or HF and negative programmed electrical stimulation ICD therapy may be considered",
"       </td>",
"       <td>",
"        IIb",
"       </td>",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        Consider ILR to help define the nature of unexplained syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In patients with non-ischaemic cardiomyopathy without severely depressed LVEF or HF ICD therapy may be considered",
"       </td>",
"       <td>",
"        IIb",
"       </td>",
"       <td>",
"        C",
"       </td>",
"       <td>",
"        Consider ILR to help define the nature of unexplained syncope",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HF: heart failure; ECG: electrocardiogram; ICD: implantable cardioverter defibrillator; ILR: implantable loop recorder; LVEF: left ventricular ejection fraction; SCD: sudden cardiac death.",
"     <br>",
"      * Class of recommendation.",
"      <br>",
"       <span class=\"bullet\">",
"        &bull;",
"       </span>",
"       Level of evidence.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: European Heart Rhythm Association (EHRA), Heart Failure Association (HFA), Heart Rhythm Society (HRS), et al. Guidelines for the diagnosis and management of syncope (version 2009): the Task Force for the Diagnosis and Management of Syncope of the European Society of Cardiology (ESC). Eur Heart J 2009; 30:2631. Copyright &copy; 2009 Oxford University Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
