var title_f39_55_40828="Imaging studies shoulder";
var content_f39_55_40828=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F77001&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F77001&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Imaging studies for the evaluation of shoulder pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Imaging modality",
"       </td>",
"       <td class=\"subtitle1\">",
"        Indications",
"       </td>",
"       <td class=\"subtitle1\">",
"        Limitations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plain film radiography",
"       </td>",
"       <td>",
"        First imaging modality for all shoulder pathology",
"        <br/>",
"        <br/>",
"        Often the only imaging modality needed for:",
"        <br/>",
"        Trauma",
"        <br/>",
"        Calcific tendonitis",
"        <br/>",
"        Arthritis",
"       </td>",
"       <td>",
"        Detection of:",
"        <br/>",
"        Bone bruise",
"        <br/>",
"        Subtle fractures",
"        <br/>",
"        Soft tissue pathology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CT",
"       </td>",
"       <td>",
"        Preoperative evaluation of intra-articular fractures",
"        <br/>",
"        Post arthroplasty evaluation",
"       </td>",
"       <td>",
"        Limited soft tissue evaluation",
"        <br/>",
"        Detection of bone marrow edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MRI",
"       </td>",
"       <td>",
"        Best initial modality for evaluation of soft tissue of the shoulder (rotator cuff, biceps, tendons, bursa, labrum)",
"        <br/>",
"        Evaluation of bone marrow",
"       </td>",
"       <td>",
"        Lower sensitivity for evaluation of shoulder instability/labral tear compared to MRA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone scan",
"       </td>",
"       <td>",
"        Infection after arthroplasty",
"        <br/>",
"        Suspected metastases",
"       </td>",
"       <td>",
"        Lack of anatomic localization and detail",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\" rowspan=\"1\">",
"        Arthrography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Conventional",
"       </td>",
"       <td class=\"sublist_other\">",
"        Diagnosis and treatment of frozen shoulder (adhesive capsulitis)",
"       </td>",
"       <td class=\"sublist_other\">",
"        Invasive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        MR arthrography",
"       </td>",
"       <td class=\"sublist_other\">",
"        Procedure of choice for evaluating shoulder instability/labral tear",
"        <br/>",
"        High suspicion of rotator cuff tear with normal MRI",
"       </td>",
"       <td class=\"sublist_other\">",
"        Invasive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        CT arthrography",
"       </td>",
"       <td class=\"sublist_other\">",
"        Rotator cuff tear, when MRI is not available or is contraindicated",
"        <br/>",
"        Shoulder instability/labral tear when MR arthrography is not available or is contraindicated",
"       </td>",
"       <td class=\"sublist_other\">",
"        Invasive",
"        <br/>",
"        Lower sensitivity for evaluation of shoulder instability/labral tear compared to MR arthrography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ultrasonography",
"       </td>",
"       <td>",
"        Evaluation of:",
"        <br/>",
"        Rotator cuff tendons",
"        <br/>",
"        Biceps tendon",
"        <br/>",
"        Calcific tendonitis",
"        <br/>",
"        Subacromial space",
"        <br/>",
"        Muscle",
"        <br/>",
"        <br/>",
"        Interventional therapeutic procedures",
"       </td>",
"       <td>",
"        Not widely used or readily available in the United States",
"        <br/>",
"        Highly operator dependent",
"        <br/>",
"        Limited evaluation of very small or very large (&gt;3 cm ) tears and for partial thickness tears",
"        <br/>",
"        Limited evaluation of the labrum and bony structures",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CT: computed tomography; MRI: magnetic resonance imaging; MR arthrography: magnetic resonance arthrography; CT arthrography: computed tomography arthrography.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
