" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Intellectual disability (mental retardation) in children: Management; outcomes; and prevention",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/59/40887/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?39/59/40887/contributors\">",
"     Penelope Pivalizza, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/59/40887/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
