" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Heparin-induced thrombocytopenia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/59/40890/contributors\">",
