" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Rotavirus vaccines",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?39/62/40937/contributors\">",
