var title_f4_11_4285="TNM perihilar cholangio";
var content_f4_11_4285=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F51131&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F51131&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging system for perihilar cholangiocarcinoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Primary tumor (T)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td colspan=\"3\">",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td colspan=\"3\">",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Tis",
"      </td>",
"      <td colspan=\"3\">",
"       Carcinoma in situ",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor confined to the bile duct, with extension up to the muscle layer or fibrous tissue",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2a",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades beyond the wall of the bile duct to surrounding adipose tissue",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2b",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades adjacent hepatic parenchyma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades unilateral branches of the portal vein or hepatic artery",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor invades main portal vein or its branches bilaterally; or the common hepatic artery; or the second-order biliary radicals bilaterally; or unilateral second-order biliary radicals with contralateral portal vein or hepatic artery involvement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Regional lymph nodes (N)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td colspan=\"3\">",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N1",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph node metastasis (including nodes along the cystic duct, common bile duct, hepatic artery, and portal vein)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N2",
"      </td>",
"      <td colspan=\"3\">",
"       Metastasis to periaortic, pericaval, superior mesenteric artery, and/or celiac artery lymph nodes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Distant metastasis (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td colspan=\"3\">",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M1",
"      </td>",
"      <td colspan=\"3\">",
"       Distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Anatomic stage/prognostic groups",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage 0",
"      </td>",
"      <td>",
"       Tis",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage I",
"      </td>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage II",
"      </td>",
"      <td>",
"       T2a-b",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIIA",
"      </td>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IIIB",
"      </td>",
"      <td>",
"       T1-3",
"      </td>",
"      <td>",
"       N1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stage IVA",
"      </td>",
"      <td>",
"       T4",
"      </td>",
"      <td>",
"       N0-1",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Stage IVB",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       N2",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
