" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of latent tuberculosis infection in HIV-infected patients",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/12/4298/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/12/4298/contributors\">",
"     Dick Menzies, MD, MSc",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/12/4298/contributors\">",
"     Section Editor",
