var title_f4_13_4315="Hypertonia in infantile colic";
var content_f4_13_4315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F78133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F78133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Features of hypertonia in infants with colic",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       The face of the baby is flushed, with occasional circumoral pallor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The abdomen is distended and tense",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The legs are drawn up on the abdomen and the feet are often cold (the legs may extend periodically during forceful cries)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The fingers are clenched",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The arms are stiff, tight, and extended (the elbows may also be flexed)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The back is arched",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Lester, BM. Colic and Excessive Crying. Report of the 105th Ross Conference on Pediatric Research, Lester, BM, Barr, RG (Eds), Ross Products Division, Columbus, OH, 1997. p. 18.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
