var title_f4_13_4316="ARA criteria for RA";
var content_f4_13_4316=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F71659&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F71659&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    1987 American College of Rheumatology (formerly American Rheumatism Association) revised classification criteria for rheumatoid arthritis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Criterion",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Morning stiffness",
"       </td>",
"       <td>",
"        Morning stiffness in and around the joints, lasting at least one hour before maximal improvement.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Arthritis of 3 or more joint areas",
"       </td>",
"       <td>",
"        At least 3 joint areas (out of 14 possible areas; right or left PIP, MCP, wrist, elbow, knee, ankle, MTP joints) simultaneously have had soft- tissue swelling or fluid (not bony overgrowth alone) as observed by a physician.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Arthritis of hand joints",
"       </td>",
"       <td>",
"        At least one area swollen (as defined above) in a wrist, MCP, or PIP joint.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symmetric arthritis",
"       </td>",
"       <td>",
"        Simultaneous involvement of the same joint areas (as defined above) on both sides of the body (bilateral involvement of PIPs, MCPs, or MTPs, without absolute symmetry is acceptable).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rheumatoid nodules",
"       </td>",
"       <td>",
"        Subcutaneous nodules over bony prominences or extensor surfaces, or in juxta-articular regions as observed by a physician.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum rheumatoid factor",
"       </td>",
"       <td>",
"        Demonstration of abnormal amounts of serum rheumatoid factor by any method for which the result has been positive in less than 5 percent of normal control subjects.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radiographic changes",
"       </td>",
"       <td>",
"        Radiographic changes typical of rheumatoid arthritis on posteroanterior hand or wrist radiographs, which must include erosions or unequivocal bony decalcification localised in, or most marked adjacent to, the involved joints (osteoarthritis changes alone do not qualify).",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: For classification purposes, a patient has RA if at least four of these criteria are satisfied (the first four must have been present for at least six weeks).",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
