var title_f4_14_4332="Age related shoulder pain";
var content_f4_14_4332=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F61048&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F61048&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Age-related causes and clinical characteristics of intrinsic shoulder pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical characteristics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Adolescents and young adults",
"       </td>",
"       <td>",
"        Overuse injuries",
"       </td>",
"       <td>",
"        Pain and loss of function associated with a particular athletic activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acromioclavicular sprain",
"       </td>",
"       <td>",
"        Focal pain over acromioclavicular joint; history of trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shoulder instability",
"       </td>",
"       <td>",
"        Minor trauma, high risk of recurrence",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"7\">",
"        Middle-aged and older individuals",
"       </td>",
"       <td>",
"        Rotator cuff tendinopathy or impingement syndrome",
"       </td>",
"       <td>",
"        Pain, difficulty with active abduction and external rotation, pain at night, crepitus may be felt with lifting arm beyond 60&deg; in impingement syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rotator cuff tears",
"       </td>",
"       <td>",
"        Pain and inability to actively abduct the arm, passive abduction is preserved",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subacromial bursitis or inflammatory synovitis",
"       </td>",
"       <td>",
"        Features resemble rotator cuff tendinopathy, may be seen with rheumatoid arthritis, polymyalgia rheumatica, or crystal-induced arthritis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adhesive capsulitis (frozen shoulder)",
"       </td>",
"       <td>",
"        Pain, stiffness, and marked loss of shoulder motion; risk factors include diabetes mellitus and prolonged immobilization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bicipital tendinitis",
"       </td>",
"       <td>",
"        Pain and tenderness anteriorly within the bicipital groove",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osteoarthritis",
"       </td>",
"       <td>",
"        Associated with damage to the rotator cuff, rheumatoid arthritis, or chondrocalcinosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myofascial pain",
"       </td>",
"       <td>",
"        Diffuse soft tissue tenderness in the shoulder region and over the chest wall",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
