var content_f4_14_4334=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F80757%7EPULM%2F62236&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F80757%7EPULM%2F62236&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Occupational and environmental exposures associated with interstitial lung disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Inhaled inorganic dust",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Silicates",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Silica (\"silicosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asbestos (\"asbestosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Talc (hydrated Mg silicates; \"talcosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Kaolin or \"china clay\" (hydrated aluminum silicate)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diatomaceous earth (Fuller's earth, aluminum silicate",
"with Fe and Mg)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Nepheline (hard rock containing mixed silicates)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aluminum silicates (sericite, sillimanite, zeolite)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Portland cement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mica (principally K and Mg aluminum silicates)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Beryllium (\"berylliosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Carbon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Coal dust (\"coal worker's pneumoconiosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Graphite (\"carbon pneumoconiosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Metals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tin (\"stannosis\")&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Aluminum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Powdered aluminum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Bauxite (aluminum oxide)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Hard metal dusts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Cadmium&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Titanium oxide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Tungsten",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Hafnium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Niobium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Cobalt",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Vanadium carbides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iron (\"siderosis\", \"arc welder's lung\") Barium (powder",
"of baryte or BaSO4; \"baritosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antimony (oxides and alloys)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematite (mixed dusts of iron oxide, silica and",
"silicates; \"siderosilicosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mixed dusts of silver and iron oxide",
"(\"argyrosiderosis\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CuSO4 neutralized with hydrated lime (Bordeaux mixture;",
"\"vineyard sprayer's lung\")",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rare earths (cerium, scandium, yttrium, lanthanum)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Crystal, RG. Interstitial lung disease. In: Wyngaarden, JB, Smith, LH, Jr, Bennett, JC, (Eds), Cecil Textbook of Medicine, 19th ed, WB Saunders Co, Philadelphia, 1992.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Occupational and environmental exposures associated with interstitial lung disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Inhaled",
"organic dusts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        (hypersensitivity pneumonitis)",
"(Partial list)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"1\">",
"        Thermophilic fungi (ie,",
"Macropolyspora faeni, Thermactinomyces vulgaris, T. sacchari)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Farmer's lung&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Grain handler's lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Humidifier or air conditioner",
"lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Bacteria (ie, Bacillus subtilis, B. cereus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Humidifier lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        True fungi (ie, Aspergillus,",
"Cryptostroma corticale, Aureobasidium pullulans, Penicillium species)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Animal proteins (eg, bird fancier's disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Inhaled agents other",
"than inorganic or organic dusts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Chemical sources",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Synthetic - fiber lung (Orlon,",
"polyesters, nylon, acrylic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bakelite worker's lung",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Vinyl chloride, polyvinyl",
"chloride powder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Gases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Oxygen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Oxides of nitrogen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Sulfur dioxide",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Chlorine gas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Methyl isocyanate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Fumes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"1\">",
"        Oxides of",
"zinc, copper, manganese, cadmium, iron, magnesium, nickel, brass,",
"selenium, tin, and antimony",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Diphenylmethane diisocyanate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Trimellitic anhydride toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Vapors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hydrocarbons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Thermosetting resins (rubber",
"tire workers)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Toluene diisocyanate (TDI -",
"asthmatic reactions prominent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Mercury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Aerosols",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Oils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Fats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pyrethrum (a natural insecticide)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Crystal, RG. Interstitial lung disease. In: Wyngaarden, JB, Smith, LH, Jr, Bennett, JC, (Eds), Cecil Textbook of Medicine, 19th ed, WB Saunders Co, Philadelphia, 1992.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
