var title_f4_17_4381="Safe food handling practices";
var content_f4_17_4381=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F73216&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F73216&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Tips for safe food handling to avoid microbial food-borne illness (food poisoning)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Purchase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not buy precooked food that is stored adjacent to raw food, even if stored on ice.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not buy canned goods that are dented, cracked, or have a bulging lid.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Storage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Make sure meat and poultry products are refrigerated when purchased.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use plastic bags to keep drippings from packages of meat and fish from contact with other foods.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Store perishable items in the refrigerator within one hour of purchase.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Maintain home refrigerator temperature between 32 and 40&deg;F (0 and 4&deg;C) and freezer temperature at &le;0&deg;F (-18&deg;C).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Freeze meat and poultry that will not be cooked within 48 hours.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Freeze tuna, bluefish, and mahi-mahi that will not be cooked within 24 hours; other fish can be stored in the refrigerator for 48 hours.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not store eggs on the refrigerator door (warmest part of the refrigerator).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refrigerate cooked foods within two hours of preparation.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Divide leftovers into small portions and store in shallow containers.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reheat leftovers to 165&deg;F (74&deg;C).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Preparation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wash hands with soap and water before food preparation and after handling raw meat, poultry, fish, and uncooked eggs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thaw frozen meats and fish in the refrigerator or microwave, not at room temperature.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marinate foods in the refrigerator, not at room temperature.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid contact of cooked foods with contaminated utensils, plates, or food preparation surfaces.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wash utensils, plates, and cutting surfaces with soap and water after contact with raw meat, poultry, fish, or eggs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid contact of juices from uncooked meat, poultry, or fish with cooked foods or foods that will be eaten raw.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thoroughly wash all fresh fruits and vegetables.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Avoid recipes using raw eggs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cooking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use a meat thermometer to monitor internal cooking temperatures; cook beef and lamb to an internal temperature of 145&deg;F (63&deg;C), pork and ground beef to 160&deg;F (71&deg;C), and poultry to 170 to 180&deg;F&nbsp;(77 to 82&deg;C).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cook eggs until the yolk begins to harden.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Boil marinade from raw meat or fish before using it on cooked food.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Serving",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serve cooked products on clean plates with clean utensils.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Keep hot foods at 140&deg;F (60&deg;C) and cold foods below 40&deg;F (4&deg;C).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Never leave foods at room temperature longer than two hours (one hour if the ambient temperature is &gt;90&deg;F (32&deg;C)).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use coolers and ice packs to transport perishable foods away from home.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
