" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of non-falciparum malaria",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Jason D Maguire, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/18/4394/contributors\">",
"     J Kevin Baird, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Johanna Daily, MD, MSc",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?4/18/4394/contributors\">",
"     Elinor L Baron, MD, DTMH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
