var title_f4_20_4430="Knee examination part I";
var content_f4_20_4430=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F53905%7EPC%2F64541&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F53905%7EPC%2F64541&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Detailed examination of the knee part I",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Examination",
"signs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pressure of the patella against the femur causing",
"retropatellar pain and crepitation**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        #1 - Patellofemoral syndrome",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Bilateral sunrise views taken",
"on one cassette to evaluate for patellar subluxation and arthritic",
"changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patellar clicking with passive flexion",
"and extension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Positive",
"Install's maneuver (retropatellar pain with quadriceps contraction)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medial joint line tenderness or spurring or both**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        #2 - Medial compartment",
"osteoarthritis",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Standing",
"AP of the knees on one cassette, showing narrowing of the articular",
"cartilage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stiffness or",
"incomplete flexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Crepitation",
"with passive flexion and extension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heel",
"to buttock measurement differs side to side**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Knee effusion",
"       </td>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Aspiration and laboratory",
"analysis for cell count, differential, crystals, gram stain, and culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Loss",
"of the peripatellar dimples",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Synovial",
"milking sign",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ballotement sign",
"for large effusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distension",
"of suprapatellar pouch",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cystic",
"swelling over the antero- inferior portion of the patella**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Prepatellar bursitis",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Aspiration and laboratory",
"analysis for cell count, differential, crystals, gram stain, and culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No",
"loss of range of motion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thickening of the prepatellar bursa**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Chronic prepatellar bursitis",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Local anesthetic block in the",
"bursa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No loss of range of",
"motion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lateral joint line",
"tenderness or spurring or both**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Lateral compartment osteoarthritis",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Standing AP of the knees on",
"one cassette showing narrowing of the articular cartilage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stiffness",
"or incomplete flexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Crepitation",
"with passive flexion and extension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenderness",
"over the medial tibial plateau, 1 and 1/2\" below the joint**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Anserine bursitis",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Local anesthetic block in the",
"bursa 2 to 3 mm above the periosteum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No",
"pain with valgus stress testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal",
"knee range of motion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A line of",
"tenderness over the medial tibial plateau extending up to or above the",
"medial joint line**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Medial",
"collateral ligament injury",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Empiric treatment with 3 to 4 weeks of straight",
"leg immobilization;",
"local anesthetic block placed just over the ligament (optional)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain aggravated by valgus stress testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain",
"aggravated by external rotation of the tibia on the femur",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ** is used to identify the hallmark sign; that is, the sign that has the greatest specificity for the diagnosis listed.",
"     <br>",
"      #1 and #2 refer to the two most common conditions affecting the knee.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Detailed examination of the knee part II",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Examination signs",
"       </td>",
"       <td class=\"subtitle1\">",
"        Diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenderness over the lateral tibial plateau extending along a line to the lateral joint line**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Lateral collateral ligament injury",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Improvement with 3 to 4 weeks of straight leg immobilization; local anesthetic block placed just over the ligament (optional)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain aggravated by varus stress testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain aggravated by internal rotation of the tibia on the femur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Painful anterior drawer sign**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Anterior cruciate ligament injury",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Arthroscopy or MRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Painful or inability to fully squat or duck waddle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Joint line tenderness and mechanical locking or joint line popping**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Meniscal tear",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Arthroscopy or MRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability to duck waddle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Popping with squatting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Palpable cyst in the medial side of the popliteal fossa**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Baker's cyst",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Characteristic aspirate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Incomplete range of motion if the cyst is large",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distal edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patellar apprehension sign with lateral subluxation**",
"       </td>",
"       <td>",
"        Patellar subluxation or dislocation",
"       </td>",
"       <td>",
"        Clinical diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lateral femoral condyle tenderness**",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Iliotibial band syndrome",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Local anesthetic block",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal lateral collateral ligament examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No active evidence of lateral compartment osteoarthritis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ** Is used to identify the hallmark sign; that is, the sign that has the greatest specificity for the diagnosis listed.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
