var title_f4_22_4461="Initial coma eval and Rx childr";
var content_f4_22_4461=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F62416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F62416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Emergent evaluation and management of stupor and coma in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       EVALUATION",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Vital signs and general and trauma examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Neurologic examination and GCS",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Screening laboratories (CBC, glucose, electrolyes, BUN, creatinine, blood cultures, LFTs, urinalysis, urine drug screen)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Head CT scan: do emergently if focal neurologic signs, papilledema, fever - consider rapid MRI instead if available.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lumbar puncture: do emergently after CT scan if fever, elevated WBC, meningismus; otherwise do according to level of suspicion for diagnosis or if cause remains obscure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Other laboratory tests: for metabolic conditons (see text), coagulation tests, carboxyhemoglobin, specific drug concentrations - do according to level of suspicion for diagnosis or if cause remains obscure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\">",
"       EEG: for possible nonconvulsive seizure, or if diagnosis remains obscure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Brain MRI with DWI, if cause remains obscure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       MANAGEMENT",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       ABCs:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Intubate if GCS &le;8 or respiratory failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Stabilize cervical spine",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Supplement O2",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       IV access",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Blood pressure support as needed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Dextrose 0.25 g/kg (2.5 mL/kg of 10 percent dextrose solution) after blood glucose drawn, before results back; do NOT delay pending results",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Treat definite seizures. Initial treatment with lorazepam (0.1 mg/kg, maximum single dose 5 mg). If seizures continue treat as for status epilepticus.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Empiric treatments:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       For possible infection:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Ceftriaxone 100 mg/kg (maximum single dose 2 grams) and Vancomycin (age-specific dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Acyclovir (age-specific dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       For possible ingestion:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Naloxone 0.1 mg/kg IV in patients up to 20 kg or &le; 5 years; maximum 2 mg IV (use if opioid toxidrome: miosis, respiratory depression, hypotonia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       For possible increased ICP:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Mannitol 0.5 to 1 gram/kg IV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2_start\">",
"       For possible nonconvulsive status:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Lorazepam (0.1 mg/kg, maximum single dose 5 mg). If suspicion of seizures continue treat as for status epilepticus.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist2\">",
"       Fosphenytoin (10 to 20 PE equivalents/kg). If suspicion of seizures continue treat as for status epilepticus.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     GCS: Glasgow coma scale; CBC: complete blood count; BUN: blood urea nitrogen; PT: prothrombin time; PTT: partial thromboplastin time; ABG: arterial blood gas; LFT: liver function tests; ECG: electrocardiogram; CT: computed tomography; WBC: white blood cells; EEG: electroencephalography; MRI: magnetic resonance imaging; DWI: diffusion weighted imaging; ICP: intracranial pressure.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
