" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Intestinal tapeworms",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/26/4519/contributors\">",
