var title_f4_27_4539="Avoiding warfarin assoc ICH";
var content_f4_27_4539=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F74392&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F74392&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Avoiding ICH during warfarin therapy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Elderly patients and those with intrinsic cerebrovascular disease are at high risk.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Keep INRs &le;3.0.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Warfarin combined with aspirin should be used with caution in elderly patients and those with cerebrovascular disease and the benefits versus risks carefully weighed.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Modest blood pressure lowering profoundly reduces ICH risk; systolic blood pressure should optimally be lowered to &lt;140 mmHg.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Hart RG, Tonarelli SB, Pearce LA. Avoiding central nervous system bleeding during antithrombotic therapy: Recent data and ideas. Stroke 2005; 36:1588. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
