var title_f4_31_4603="Criteria for preeclampsia";
var content_f4_31_4603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F79977&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F79977&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for the diagnosis of preeclampsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Systolic blood pressure &ge;140 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diastolic blood pressure &ge;90 mmHg*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Proteinuria &ge;0.3 grams in a 24-hour urine specimen",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The elevation in blood pressure should be sustained, which is generally regarded as two measurements at least six hours, but no more than seven days, apart.",
"    <div class=\"footnotes\">",
"     * Diastolic blood pressure is determined based upon the fifth Korotkoff sound (disappearance) with patient sitting.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     A random urine protein determination of 30 mg/dL or 1+ on dipstick is suggestive, but not diagnostic, of the presence of this criterion.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
