var script_f4_32_4609=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function IPS_fx() {",
"with(document.IPS_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4.checked){",
"Score = Score + 1;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 0)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 1) && (Score <= 1)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 2) && (Score <= 2)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 3)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"if ((Score >= 4) && (Score <= 4)){ document.getElementById('rr1_5').bgColor = '#cccccc';",
"}",
"if ((Score >= 5) && (Score <= 7)){ document.getElementById('rr1_6').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"document.getElementById('rr1_5').bgColor = \"\";",
"document.getElementById('rr1_6').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
