" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Intrahepatic cholestasis of pregnancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?4/32/4617/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
