var title_f4_32_4620="Management of asbestosis";
var content_f4_32_4620=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F78800&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F78800&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for management after diagnosis of asbestosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        1. Patient notification",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1.1. Inform patient of work-related illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1.2. Report to appropriate authority as occupational",
"disease, as required by law",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1.3. Inform patient that there are options for",
"compensation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        2. Impairment",
"assessment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2.1. Conduct an assessment of functional impairment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2.2. Rate impairment in accordance with ATS criteria,",
"which are incorporated into the AMA Guides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        3. Tertiary",
"prevention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3.1. Smoking cessation (primary prevention for",
"smoking-related disorders)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3.2. Withdrawal from further excessive exposure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3.3. Immunization (pneumococcal pneumonia, influenza)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3.4. Management of concurrent respiratory and other",
"diseases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        4. Monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4.1. Chest film and pulmonary function testing should",
"be conducted every 3 to 5 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4.2. Active monitoring (periodic screening) for colon",
"cancer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4.3. Observation and elevated index of suspicion but",
"not screening for lung cancer, mesothelioma, gastrointestinal cancers",
"(other than colon)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        5. Development of a",
"patient-specific management plan for symptomatic disease",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AMA: American Medical Association; ATS: American Thoracic Society.",
"    </div>",
"    <div class=\"reference\">",
"     Am J Respir Crit Care Med vol 170 pp 692, 2004; www.atsjournals.org.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
